part of '../userApi.dart';extension GetUser on UserApi {  Future<Map<String, dynamic>> getUser(String token) async {    try {      final options = Options(        headers: {          HttpHeaders.authorizationHeader:              token.isNotEmpty ? 'Bearer $token' : null,        },      );      final response =          await dio.get<dynamic>('api/users/getUser', options: options);      return response.data as Map<String, dynamic>;    } on DioException catch (e) {      throw CustomException(e.error);    }  }}