part of '../userApi.dart';extension Login on UserApi {  Future<Map<String, dynamic>> login({    required String email,    required String password,  }) async {    try {      final response = await dio.post<dynamic>(        'api/users/login',        data: {          'email': email,          'password': password,        },      );      return response.data as Map<String, dynamic>;    } on DioException catch (e) {      throw CustomException(e.error);    }  }}