part of '../userApi.dart';extension Register on UserApi {  Future<String> register({    required User user,    required String password,  }) async {    try {      final formData = FormData.fromMap({        'name': user.name,        'email': user.email,        'password': password,        'university': user.userProps.university,        'school': user.userProps.school,        'major': user.userProps.major,        'contact': user.userProps.contact,        'image': user.userProps.image,      });      if (user.userProps.image.isNotEmpty) {        formData.files.add(          MapEntry(            'image',            await MultipartFile.fromFile(              user.userProps.image,              filename: user.userProps.image,              contentType: MediaType('image', 'jpg/jpeg/webp/png'),            ),          ),        );      }      final response = await dio.post<dynamic>(        'api/users/register',        data: formData,      );      return response.data as String;    } on DioException catch (e) {      throw CustomException(e.error);    }  }}