part of '../userApi.dart';extension ResetPassword on UserApi {  Future<String> resetPassword(String email) async {    try {      final response = await dio.post<dynamic>(        'api/users/resetPassword',        data: {          'email': email,        },      );      return response.data as String;    } on DioException catch (e) {      throw CustomException(e.error);    }  }}extension ResetPasswordVerify on UserApi {  Future<String> resetPasswordVerify(String email, String otp) async {    try {      final response = await dio.post<dynamic>(        'api/users/resetPasswordVerify',        data: {          'email': email,          'otp': otp,        },      );      return response.data as String;    } on DioException catch (e) {      throw CustomException(e.error);    }  }}